unit.struct:
struct accept_range {
uint8_t lo;
uint8_t hi;
} accept_ranges[16] = {

{ LOCB, HICB },
{ 0xA0, HICB },
{ LOCB, 0x9F },
{ 0x90, HICB },
{ LOCB, 0x8F },
};

function.block:
{
size_t i;
int n;
file_unichar_t c;
int gotone = 0, ctrl = 0;

if (ubuf)
*ulen = 0;

for (i = 0; i < nbytes; i++) {
if ((buf[i] & 0x80) == 0) {





if (text_chars[buf[i]] != T)
ctrl = 1;

if (ubuf)
ubuf[(*ulen)++] = buf[i];
} else if ((buf[i] & 0x40) == 0) {
return -1;
} else {
int following;
uint8_t x = first[buf[i]];
const struct accept_range *ar =
&accept_ranges[(unsigned int)x >> 4];
if (x == XX)
return -1;

if ((buf[i] & 0x20) == 0) {
c = buf[i] & 0x1f;
following = 1;
} else if ((buf[i] & 0x10) == 0) {
c = buf[i] & 0x0f;
following = 2;
} else if ((buf[i] & 0x08) == 0) {
c = buf[i] & 0x07;
following = 3;
} else if ((buf[i] & 0x04) == 0) {
c = buf[i] & 0x03;
following = 4;
} else if ((buf[i] & 0x02) == 0) {
c = buf[i] & 0x01;
following = 5;
} else
return -1;

for (n = 0; n < following; n++) {
i++;
if (i >= nbytes)
goto done;

if (n == 0 &&
(buf[i] < ar->lo || buf[i] > ar->hi))
return -1;

if ((buf[i] & 0x80) == 0 || (buf[i] & 0x40))
return -1;

c = (c << 6) + (buf[i] & 0x3f);
}

if (ubuf)
ubuf[(*ulen)++] = c;
gotone = 1;
}
}
done:
return ctrl ? 0 : (gotone ? 2 : 1);
}

{
if (nbytes > 3 && buf[0] == 0xef && buf[1] == 0xbb && buf[2] == 0xbf)
return file_looks_utf8(buf + 3, nbytes - 3, ubuf, ulen);
else
return -1;
}

{
if (nbytes > 4 && buf[0] == '+' && buf[1] == '/' && buf[2] == 'v')
switch (buf[3]) {
case '8':
case '9':
case '+':
case '/':
if (ubuf)
*ulen = 0;
return 1;
default:
return -1;
}
else
return -1;
}

{
int bigend;
size_t i;

if (nbytes < 2)
return 0;

if (bf[0] == 0xff && bf[1] == 0xfe)
bigend = 0;
else if (bf[0] == 0xfe && bf[1] == 0xff)
bigend = 1;
else
return 0;

*ulen = 0;

for (i = 2; i + 1 < nbytes; i += 2) {


if (bigend)
ubf[(*ulen)++] = bf[i + 1]
| (CAST(file_unichar_t, bf[i]) << 8);
else
ubf[(*ulen)++] = bf[i]
| (CAST(file_unichar_t, bf[i + 1]) << 8);

if (ubf[*ulen - 1] == 0xfffe)
return 0;
if (ubf[*ulen - 1] < 128 &&
text_chars[CAST(size_t, ubf[*ulen - 1])] != T)
return 0;
}

return 1 + bigend;
}

{
int bigend;
size_t i;

if (nbytes < 4)
return 0;

if (bf[0] == 0xff && bf[1] == 0xfe && bf[2] == 0 && bf[3] == 0)
bigend = 0;
else if (bf[0] == 0 && bf[1] == 0 && bf[2] == 0xfe && bf[3] == 0xff)
bigend = 1;
else
return 0;

*ulen = 0;

for (i = 4; i + 3 < nbytes; i += 4) {


if (bigend)
ubf[(*ulen)++] = CAST(file_unichar_t, bf[i + 3])
| (CAST(file_unichar_t, bf[i + 2]) << 8)
| (CAST(file_unichar_t, bf[i + 1]) << 16)
| (CAST(file_unichar_t, bf[i]) << 24);
else
ubf[(*ulen)++] = CAST(file_unichar_t, bf[i + 0])
| (CAST(file_unichar_t, bf[i + 1]) << 8)
| (CAST(file_unichar_t, bf[i + 2]) << 16)
| (CAST(file_unichar_t, bf[i + 3]) << 24);

if (ubf[*ulen - 1] == 0xfffe)
return 0;
if (ubf[*ulen - 1] < 128 &&
text_chars[CAST(size_t, ubf[*ulen - 1])] != T)
return 0;
}

return 1 + bigend;
}

{
size_t i;

for (i = 0; i < nbytes; i++) {
out[i] = ebcdic_to_ascii[buf[i]];
}
}
