archive_entry_free(ae);
for (i = 0; i < 256; i++) {
int j;
if (i == 0) {
for (j = 0; j < (int)sizeof(data); j++)
data[j] = (i^j) & 0xff;
} else {
for (j = 0; j < (int)sizeof(data); j++)
data[j] ^= i+j;
}
assertEqualIntA(a, 1024, archive_write_data(a, data, 1024));
}




