unit.struct:
struct interval {
wchar_t first;
wchar_t last;
};

function.block:
{
int min = 0;
int mid;

if (ucs < table[0].first || ucs > table[max].last)
return 0;
while (max >= min) {
mid = (min + max) / 2;
if (ucs > table[mid].last)
min = mid + 1;
else if (ucs < table[mid].first)
max = mid - 1;
else
return 1;
}

return 0;
}

{
int w, width = 0;

for (;*pwcs && n-- > 0; pwcs++)
if ((w = mk_wcwidth(*pwcs)) < 0)
return -1;
else
width += w;

return width;
}

